content.content:
  1→name: Deploy Recruiter App
  2→
  3→# Trigger rebuild with corrected HTTPS URLs - July 8, 2025
  4→
  5→on:
  6→  push:
  7→    branches: [main, master]
  8→    paths:
  9→      - "flowterview-backend/**"
 10→      - "flowterview-app/**"
 11→      - ".github/workflows/recruiter-app-deploy.yml"
 12→
 13→jobs:
 14→  build-and-deploy:
 15→    runs-on: ubuntu-latest
 16→
 17→    steps:
 18→      - name: Checkout code
 19→        uses: actions/checkout@v3
 20→
 21→      - name: Set up Docker Buildx
 22→        uses: docker/setup-buildx-action@v2
 23→
 24→      - name: Login to Docker Hub
 25→        uses: docker/login-action@v2
 26→        with:
 27→          username: ${{ secrets.DOCKER_USERNAME }}
 28→          password: ${{ secrets.DOCKER_PASSWORD }}
 29→
 30→      - name: Build and push backend image
 31→        uses: docker/build-push-action@v4
 32→        with:
 33→          context: ./flowterview-backend
 34→          push: true
 35→          tags: sivera/sivera:recruiter-backend
 36→          cache-from: type=registry,ref=sivera/sivera:recruiter-backend-cache
 37→          cache-to: type=registry,ref=sivera/sivera:recruiter-backend-cache,mode=max
 38→          build-args: |
 39→            SUPABASE_URL=${{ secrets.SUPABASE_URL }}
 40→            SUPABASE_KEY=${{ secrets.SUPABASE_KEY }}
 41→            DAILY_API_KEY=${{ secrets.DAILY_API_KEY }}
 42→
 43→      - name: Build and push frontend image
 44→        uses: docker/build-push-action@v4
 45→        with:
 46→          context: ./flowterview-app
 47→          push: true
 48→          tags: sivera/sivera:recruiter-frontend
 49→          cache-from: type=registry,ref=sivera/sivera:recruiter-frontend-cache
 50→          cache-to: type=registry,ref=sivera/sivera:recruiter-frontend-cache,mode=max
 51→          build-args: |
 52→            NEXT_PUBLIC_SUPABASE_URL=${{ secrets.NEXT_PUBLIC_SUPABASE_URL }}
 53→            NEXT_PUBLIC_SUPABASE_ANON_KEY=${{ secrets.NEXT_PUBLIC_SUPABASE_ANON_KEY }}
 54→            DATABASE_URL=${{ secrets.DATABASE_URL }}
 55→            NEXT_PUBLIC_SITE_URL=${{ secrets.NEXT_PUBLIC_RECRUITER_SITE_URL }}
 56→            NEXT_PUBLIC_SIVERA_BACKEND_URL=${{ secrets.NEXT_PUBLIC_SIVERA_BACKEND_URL }}
 57→            NEXT_PUBLIC_CORE_BACKEND_URL=${{ secrets.NEXT_PUBLIC_CORE_BACKEND_URL }}
 58→            NEXT_PUBLIC_AWS_CDN=${{ secrets.NEXT_PUBLIC_AWS_CDN }}
 59→
 60→      - name: Deploy to AWS EC2
 61→        uses: appleboy/ssh-action@v1.0.0
 62→        with:
 63→          host: ${{ secrets.AWS_RECRUITER_APP_HOST }}
 64→          username: ubuntu
 65→          key: ${{ secrets.AWS_SSH_PRIVATE_KEY }}
 66→          script_stop: true
 67→          script: |
 68→            mkdir -p /home/ubuntu/sivera-recruiter-application
 69→            sudo chown -R ubuntu:ubuntu /home/ubuntu/sivera-recruiter-application
 70→            sudo chmod -R 755 /home/ubuntu/sivera-recruiter-application
 71→
 72→            DOCKER_COMPOSE="docker-compose"
 73→
 74→            echo "${{ secrets.DOCKER_PASSWORD }}" | docker login -u "${{ secrets.DOCKER_USERNAME }}" --password-stdin
 75→
 76→            cd /home/ubuntu/sivera-recruiter-application
 77→            $DOCKER_COMPOSE pull || true
 78→            $DOCKER_COMPOSE down || true
 79→
 80→            cat > .env.backend << EOF
 81→            SMTP_HOST=${{ secrets.SMTP_HOST }}
 82→            SMTP_PORT=${{ secrets.SMTP_PORT }}
 83→            SMTP_USER=${{ secrets.SMTP_USER }}
 84→            SMTP_PASS=${{ secrets.SMTP_PASS }}
 85→            SUPABASE_URL=${{ secrets.SUPABASE_URL }}
 86→            SUPABASE_KEY=${{ secrets.SUPABASE_KEY }}
 87→            DAILY_API_KEY=${{ secrets.DAILY_API_KEY }}
 88→            GEMINI_API_KEY=${{ secrets.GEMINI_API_KEY }}
 89→            OPENAI_API_KEY=${{ secrets.OPENAI_API_KEY }}
 90→            GROQ_API_KEY=${{ secrets.GROQ_API_KEY }}
 91→            AWS_ACCESS_KEY_ID=${{ secrets.AWS_ACCESS_KEY_ID }}
 92→            AWS_SECRET_ACCESS_KEY=${{ secrets.AWS_SECRET_ACCESS_KEY }}
 93→            AWS_REGION=${{ secrets.AWS_REGION }}
 94→            DEEPGRAM_API_KEY=${{ secrets.DEEPGRAM_API_KEY }}
 95→            ELEVENLABS_API_KEY=${{ secrets.ELEVENLABS_API_KEY }}
 96→            ELEVENLABS_VOICE_ID=${{ secrets.ELEVENLABS_VOICE_ID }}
 97→            RIME_API_KEY=${{ secrets.RIME_API_KEY }}
 98→            RIME_VOICE_ID=${{ secrets.RIME_VOICE_ID }}
 99→            FAL_API_KEY=${{ secrets.FAL_API_KEY }}
100→            LLM_MODEL=${{ secrets.LLM_MODEL }}
101→            LLM_PROVIDER=${{ secrets.LLM_PROVIDER }}
102→            TTS_PROVIDER=google
103→            LINKEDIN_CLIENT_ID=${{ secrets.LINKEDIN_CLIENT_ID }}
104→            LINKEDIN_CLIENT_SECRET=${{ secrets.LINKEDIN_CLIENT_SECRET }}
105→            LINKEDIN_REDIRECT_URI=https://api.sivera.io/api/v1/linkedin/callback
106→            FRONTEND_URL=https://app.sivera.io
107→            RECRUITER_FRONTEND_URL=https://recruiter.sivera.io
108→            PORT=8010
109→            EOF
110→
111→            cat > .env.frontend << EOF
112→            NEXT_PUBLIC_SUPABASE_URL=${{ secrets.NEXT_PUBLIC_SUPABASE_URL }}
113→            NEXT_PUBLIC_SUPABASE_ANON_KEY=${{ secrets.NEXT_PUBLIC_SUPABASE_ANON_KEY }}
114→            DATABASE_URL=${{ secrets.DATABASE_URL }}
115→            NEXT_PUBLIC_SITE_URL=${{ secrets.NEXT_PUBLIC_RECRUITER_SITE_URL }}
116→            NEXT_PUBLIC_SIVERA_BACKEND_URL=${{ secrets.NEXT_PUBLIC_SIVERA_BACKEND_URL }}
117→            NEXT_PUBLIC_CORE_BACKEND_URL=${{ secrets.NEXT_PUBLIC_CORE_BACKEND_URL }}
118→            NEXT_PUBLIC_AWS_CDN=${{ secrets.NEXT_PUBLIC_AWS_CDN }}
119→            EOF
120→
121→            echo "# Recruiter application deployment configuration" > docker-compose.yml
122→            echo "services:" >> docker-compose.yml
123→            echo "  backend:" >> docker-compose.yml
124→            echo "    image: sivera/sivera:recruiter-backend" >> docker-compose.yml
125→            echo "    pull_policy: always" >> docker-compose.yml
126→            echo "    ports:" >> docker-compose.yml
127→            echo "      - \"8010:8010\"" >> docker-compose.yml
128→            echo "    restart: always" >> docker-compose.yml
129→            echo "    env_file:" >> docker-compose.yml
130→            echo "      - .env.backend" >> docker-compose.yml
131→            echo "  frontend:" >> docker-compose.yml
132→            echo "    image: sivera/sivera:recruiter-frontend" >> docker-compose.yml
133→            echo "    pull_policy: always" >> docker-compose.yml
134→            echo "    ports:" >> docker-compose.yml
135→            echo "      - \"3000:3000\"" >> docker-compose.yml
136→            echo "    restart: always" >> docker-compose.yml
137→            echo "    env_file:" >> docker-compose.yml
138→            echo "      - .env.frontend" >> docker-compose.yml
139→
140→            $DOCKER_COMPOSE up -d
